    address constant public NATIVE_TOKEN = address(1);
    uint constant public MAX_PROTOCOL_FEE = 100; // 100 = %100
    
    address public override treasury;
    uint public override protocolFee;
    
    mapping(address =>  mapping(uint => GhafMarketPlaceLib.Nft)) public nfts;
    // ^ Mapping from [_nftContractAddress][_tokenId] to a listed Nft
    mapping(address => mapping(uint => GhafMarketPlaceLib.Bid[])) public bids; 
    // ^ Mapping from [_nftContractAddress][_tokenId] to bids (note: it wasn't possible to define Bid[] in Nft)

}","keccak256":"0xaceaf9c462a9657e4bea42f09029201ada45b03212253abf41afdfefae68214a","license":"MIT"},"contracts/GhafMarketplace/interfaces/IGhafMarketPlaceLogic.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.8.4;

import "./IGhafMarketPlaceStorage.sol";
import "../GhafMarketPlaceLib.sol";


interface IGhafMarketPlaceLogic is IGhafMarketPlaceStorage {

  	// Events

    event NftListed(
        address nftContractAddress, 
        uint256 tokenId, 
        address seller,
        GhafMarketPlaceLib.BuyTypes buyType
    );

    event NftDelisted(
        address nftContractAddress, 
        uint256 tokenId, 
        address seller
    );

    event NewBid(
        address nftContractAddress, 
        uint256 tokenId, 
        address seller, 
        address buyer,
        uint bidAmount,
        address paymentToken,
        uint bidIdx
    );

    event BidUpdated(
        address nftContractAddress, 
        uint256 tokenId, 
        address seller, 
        address buyer, 
        uint bidIdx,
        uint newAmount
    );

    event BidAccepted(
        address nftContractAddress, 
        uint256 tokenId, 
        address seller, 
        address buyer,
        uint bidIdx
    );

    event BidCanceled(
        address nftContractAddress, 
        uint256 tokenId, 
        address seller, 
        address buyer,
        uint bidIdx
    );

    event BidRevoked(
        address nftContractAddress, 
        uint256 tokenId, 
        address seller, 
        address buyer, 
        uint bidIdx
    );

    event NftSold(
        address nftContractAddress, 
        uint256 tokenId,  
        address seller, 
        address buyer,
        uint fee,
        uint payAmount,
        address paymentToken,
        GhafMarketPlaceLib.BuyTypes buyType
    );

	// State-changing functions



    function setProtocolFee(uint _protocolFee) external;

    function setTreasury(address _treasury) external;


    function pause() external;

    function unpause() external;

	function listNft(
        address _nftContractAddress,
        uint256 _tokenId,
        GhafMarketPlaceLib.BuyTypes _buyType
	) external returns (bool);

    function delistNft(address _nftContractAddress,uint256 _tokenId) external returns (bool);

    function putBid(
        address _nftContractAddress, 
        uint256 _tokenId,
        uint _amount,
        address _paymentToken
    ) external payable returns (uint);

    function increaseBid(
        address _nftContractAddress, 
        uint256 _tokenId,
        uint _bidIdx,
        uint _newAmount
    ) external payable returns (bool);

    function cancelBid(
        address _nftContractAddress, 
        uint256 _tokenId,
        uint _bidIdx
    ) external returns (bool);

    function acceptBid(
        address _nftContractAddress, 
        uint256 _tokenId,
        uint _bidIdx
    ) external returns (bool);

    function buyNft(
        address _nftContractAddress, 
        uint256 _tokenId,
        uint _amount,
        address _paymentToken
    ) external payable returns (bool);
    

}","keccak256":"0x46bda1fa04b13bbff3f8ef383e21c49d4cc086eeb59d472af633c969e30337c3","license":"MIT"},"contracts/GhafMarketplace/interfaces/IGhafMarketPlaceStorage.sol":{"content":"// SPDX-License-Identifier: MIT
